import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
// import TextField from "@mui/material/TextField";

import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";

import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";

// import FormGroup from "@mui/material/FormGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Switch from "@mui/material/Switch";

// import InputLabel from "@mui/material/InputLabel";
import TextField from "@mui/material/TextField";

import Divider from "@mui/material/Divider";

class certification extends React.Component {
  constructor() {
    super();
    this.state = {
      items: [],
    };
  }

  render() {
    return (
      <>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Typography>
              A. CERTIFICATION OF CONSUMPTION OF BENEFITS{" "}
            </Typography>
          </Grid>
          <Grid item xs={12}>
            <Card sx={{ minWidth: 275 }}>
              <CardContent>
                <Grid container spacing={2}>
                  <Grid item xs={12}>
                    <FormControlLabel
                      control={<Checkbox />}
                      //   label={`${""} ${<br/>} (${"PhilHealth Benefits is enough to cover HCI and FF charges."})`}
                      label={
                        <>
                          <div>
                            PhilHealth Benefits is enough to cover HCI and FF
                            charges.
                          </div>
                          <div>
                            No purchase of Drug/Medicines, supplies, Diagnostics
                            and co-pay for Professional fees by the
                            member/patient
                          </div>
                        </>
                      }
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TableContainer component={Paper}>
                      <Table
                        // className={classes.table}
                        aria-label="simple table"
                      >
                        <TableHead>
                          <TableRow>
                            <TableCell align="center"></TableCell>
                            <TableCell align="center">
                              TOTAL ACTUAL CHARGES
                            </TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          <TableRow>
                            <TableCell align="">
                              <b>Total Health Care Institution Fees</b>
                            </TableCell>
                            <TableCell align="center">
                              {" "}
                              <TextField
                                id="outlined-multiline-flexible"
                                // label="Multiline"
                                // multiline
                                // maxRows={4}
                                fullWidth
                                name="pMemberPIN"
                                size="small"
                                onChange={this.props.onchange}
                              />
                            </TableCell>
                          </TableRow>
                          <TableRow>
                            <TableCell align="">
                              <b>Total Professional Fees</b>
                            </TableCell>
                            <TableCell align="center">
                              {" "}
                              <TextField
                                id="outlined-multiline-flexible"
                                // label="Multiline"
                                // multiline
                                // maxRows={4}
                                fullWidth
                                name="pMemberPIN"
                                size="small"
                                onChange={this.props.onchange}
                              />
                            </TableCell>
                          </TableRow>
                          <TableRow>
                            <TableCell align="">
                              <b>Grand Total</b>
                            </TableCell>
                            <TableCell align="center">
                              {" "}
                              <TextField
                                id="outlined-multiline-flexible"
                                // label="Multiline"
                                // multiline
                                // maxRows={4}
                                fullWidth
                                name="pMemberPIN"
                                size="small"
                                onChange={this.props.onchange}
                              />
                            </TableCell>
                          </TableRow>
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Grid>
                  <Grid item xs={12}>
                    <FormControlLabel
                      control={<Checkbox />}
                      //   label={`${""} ${<br/>} (${"PhilHealth Benefits is enough to cover HCI and FF charges."})`}
                      label={
                        <>
                          <div>
                            the benefits of the member/patient was completely
                            consumed prior to co-pay OR the benefits of the
                            member/patient is not completely consumed BUT with
                            purchases/expenses for drugs/medicine, supplies,
                            diagnostics and others.
                          </div>
                          <div>a. The total co-pay for the following are:</div>
                        </>
                      }
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TableContainer component={Paper}>
                      <Table
                        // className={classes.table}
                        aria-label="simple table"
                      >
                        <TableHead>
                          <TableRow>
                            <TableCell align="center"></TableCell>
                            <TableCell align="center">
                              TOTAL ACTUAL CHARGES
                            </TableCell>
                            <TableCell align="center">
                              Amount after applications of Sicount(i.e.,
                              personal discount, Senior Citizen/PWD)
                            </TableCell>
                            <TableCell align="center">
                              PhilHealt Benefits
                            </TableCell>
                            <TableCell align="center">
                              Amount after PhilHealth Deduction
                            </TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          <TableRow>
                            <TableCell align="">
                              <b>Total Health Care Institution Fees</b>
                            </TableCell>
                            <TableCell align="center">
                              {" "}
                              <TextField
                                id="outlined-multiline-flexible"
                                // label="Multiline"
                                // multiline
                                // maxRows={4}
                                fullWidth
                                name="pMemberPIN"
                                size="small"
                                onChange={this.props.onchange}
